import React from "react";
import DayPickerInput from 'react-day-picker/DayPickerInput';
import { DataGrid, ColDef, ValueGetterParams } from '@material-ui/data-grid';
import 'react-day-picker/lib/style.css';
import { DateUtils } from 'react-day-picker';
import dateFnsFormat from 'date-fns/format';
import dateFnsParse from 'date-fns/parse';



const columns = [
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'firstName', headerName: 'First name', width: 130 },
  { field: 'lastName', headerName: 'Last name', width: 130 },
  {
    field: 'age',
    headerName: 'Age',
    type: 'number',
    width: 90,
  },
  {
    field: 'fullName',
    headerName: 'Full name',
    description: 'This column has a value getter and is not sortable.',
    sortable: false,
    width: 160,
    valueGetter: (params) =>
      `${params.getValue('firstName') || ''} ${params.getValue('lastName') || ''}`,
  },
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
  { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

function parseDate(str, format, locale) {
	const parsed = dateFnsParse(str, format, new Date(), { locale });
	if (DateUtils.isDate(parsed)) {
	  return parsed;
	}
	return undefined;
}  
function formatDate(date, format, locale) {
	return dateFnsFormat(date, format, { locale });
}

export default class Dashboard extends React.Component {

  
  state = { Show: false, Showing: false};
  show = () => { if (this.state.Showing) return;

    this.setState({ Show: true, Showing: true });
    setTimeout(() => {
      this.setState({ Show: false, Showing: false });
    }, 3000);
  };

  
  constructor(props){
    super(props);

    this.state = {       
            email: '',      
            password:'',
            users: [],
            email_error:'', 
            email_color:'black', 
            pass_error:'',
            pass_color:'black', 
            redirect: false,
            message:'',
            pop:'',    
            selectedDay: undefined,
    };
    this.handleDayChange = this.handleDayChange.bind(this);
  
  }

  
  handleDayChange(day) {
    this.setState({ selectedDay: day });
  }

  componentDidMount() { }
  render() {
    return (
      <>
     
     



						<div class="kt-container  kt-container--fluid  kt-grid__item kt-grid__item--fluid">
							<div class="row">
								<div class="col-md-12">

								
									<div class="kt-portlet">
										

								
										<form class="kt-form">
											<div class="kt-portlet__body">
												
                        <div class="row"> 
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label>First Name</label>
                                  <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter First Name"/>
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>
                                </div>
                              </div>
                              
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label>Last Name</label>
                                  <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter Last Name"/>
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>
                                </div>
                              </div>
                              
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label>D.O.B</label>
                                 
                                  <div class="input-group date">
                                      <DayPickerInput
                                            style={{"width": "250px"}}
                                            class="form-control" 
                                            onChange={this.myChangeHandler}
                                            formatDate={formatDate}  
                                            format='dd/MM/yyyy'
                                            placeholder="DD/MM/YYYY"
                                            onDayChange={e =>{
                                              var date = new Date(e);
                                              let mnth = ("0" + (date.getMonth() + 1)).slice(-2),
                                              day = ("0" + date.getDate()).slice(-2);
                                              let dob=[day, mnth , date.getFullYear()].join("/");
                                              this.setState({dob: dob})
                                            }}
                                      />
                                  </div>

                                 
                                  
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>

                                </div>
                              </div>
                              
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label>Gender</label>
                                  <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter email"/>
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>
                                </div>
                              </div>
                        </div>

                        <div class="row"> 
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label>MRN Root</label>
                                  <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter MRN Root"/>
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>
                                </div>
                              </div>
                              
                              <div class="col-md-3">  
                                <div class="form-group">
                                  <label> MRN Extension</label>
                                  <input type="email" class="form-control" aria-describedby="emailHelp" placeholder="Enter MRN Extension"/>
                                  <span class="form-text text-muted">We'll never share your email with anyone else.</span>
                                </div>
                              </div>
                              
